PROCEDURE Proc_InsertOrganizationUnit (IN p_OrganizationUnitName varchar(255), IN p_Address text, IN p_Description text, IN p_ParentID int, IN p_OrganizationUnitCode varchar(255))
BEGIN
  SELECT
    @parentCode := (SELECT
        o.OrgCode
      FROM organizationunits o
      WHERE o.OrganizationUnitID = p_ParentID
      OR o.ParentID IS NULL
      ORDER BY o.OrgCode DESC LIMIT 1);

  SELECT
    @lastChildCode := (SELECT
        o.OrgCode
      FROM organizationunits o
      WHERE o.ParentID = p_ParentID
      ORDER BY o.OrgCode DESC LIMIT 1);

  SELECT
    @lastChildCode := COALESCE(@lastChildCode, CONCAT(@parentCode, '000/'));

  SELECT
    @lastOrgCode := SUBSTRING_INDEX (@lastChildCode, '/', -2);

  SELECT
    @lastValue := CAST(@lastOrgCode AS UNSIGNED);

  SELECT
    @lastValue := @lastValue + 1;

  SELECT
    @orgCode := CONCAT(@parentCode, LPAD(@lastValue, 3, 0), '/');


  INSERT INTO organizationunits (OrganizationUnitName, OrganizationUnitCode, ParentID, OrgCode, Address, Description)
    VALUES (p_OrganizationUnitName, p_OrganizationUnitCode, p_ParentID, @orgCode, p_Address, p_Description);
END
