--
-- Create procedure `Proc_GetNumberOfEmployeeChart`
--
CREATE
DEFINER = 'root'@'localhost'
PROCEDURE Proc_GetNumberOfEmployeeChart ()
BEGIN
  DROP TEMPORARY TABLE IF EXISTS tmp_promotion, tmp_employee, tmp_result;
  CREATE TEMPORARY TABLE IF NOT EXISTS tmp_promotion
  SELECT
    ROW_NUMBER() OVER (PARTITION BY ep.UserID
    ORDER BY FromDate DESC) AS STT,
    ep.*
  FROM employee_promotion ep;

  CREATE TEMPORARY TABLE IF NOT EXISTS tmp_employee
  SELECT
    *
  FROM users u
  WHERE u.IsDeleted IS NOT TRUE;
  -- AND FIND_IN_SET(u.OrganizationUnitID, OrganizationUnitID) > 0;

  CREATE TEMPORARY TABLE IF NOT EXISTS tmp_result
  SELECT
    MONTH(tp.FromDate) AS DateTimeValue,
    COUNT(te.UserID) AS Value
  FROM tmp_employee te
    JOIN tmp_promotion tp
      ON te.UserID = tp.UserID
  WHERE tp.STT = 1
  AND tp.EmployeeStatusID = 1
  GROUP BY MONTH(tp.FromDate)
  ORDER BY MONTH(tp.FromDate);
  SELECT
    a.Value + COALESCE(A.PreValue, 0) AS Value,
    A.DateTimeValue
  FROM (SELECT
      LAG(tr.Value, 1) OVER (
      ORDER BY tr.DateTimeValue) AS PreValue,
      tr.*
    FROM tmp_result tr) A;
END
$$

