--
-- Create procedure `Proc_GetFluctuationChart`
--
CREATE
DEFINER = 'root'@'localhost'
PROCEDURE Proc_GetFluctuationChart (IN Year int)
BEGIN

  SET @FromDate = DATE(CONCAT(Year, '-01-01'));
  SET @ToDate = DATE(CONCAT(Year, '-12-31'));

  DROP TEMPORARY TABLE IF EXISTS tmp_promotion_receive, tmp_employee, tmp_promotion_termination, tmp_receive, tmp_termination;
  CREATE TEMPORARY TABLE IF NOT EXISTS tmp_promotion_receive
  SELECT
    ROW_NUMBER() OVER (PARTITION BY ep.UserID
    ORDER BY FromDate ASC) AS STT,
    ep.*
  FROM employee_promotion ep;
  CREATE TEMPORARY TABLE IF NOT EXISTS tmp_promotion_termination
  SELECT
    ROW_NUMBER() OVER (PARTITION BY ep.UserID
    ORDER BY FromDate DESC) AS STT,
    ep.*
  FROM employee_promotion ep;
  CREATE TEMPORARY TABLE IF NOT EXISTS tmp_employee
  SELECT
    *
  FROM users u
  WHERE u.IsDeleted IS NOT TRUE;
  -- AND FIND_IN_SET(u.OrganizationUnitID, OrganizationUnitID) > 0

  -- lấy nhân viên nghỉ việc
  CREATE TEMPORARY TABLE tmp_receive
  SELECT
    MONTH(tp.FromDate) AS DateTimeValue,
    COUNT(tp.UserID) AS ReceiveValue
  FROM tmp_employee te
    JOIN tmp_promotion_receive tp
      ON te.UserID = tp.UserID
  WHERE tp.STT = 1
  AND tp.EmployeeStatusID = 1
  AND tp.FromDate BETWEEN @FromDate AND @ToDate
  GROUP BY MONTH(tp.FromDate);

  CREATE TEMPORARY TABLE tmp_termination
  SELECT
    MONTH(tp.FromDate) AS DateTimeValue,
    COUNT(tp.UserID) AS TerminationValue
  FROM tmp_employee te
    JOIN tmp_promotion_termination tp
      ON te.UserID = tp.UserID
  WHERE tp.STT = 1
  AND tp.EmployeeStatusID = 2
  AND tp.FromDate BETWEEN @FromDate AND @ToDate
  GROUP BY MONTH(tp.FromDate);


  SELECT
    tr.DateTimeValue,
    tt.TerminationValue,
    tr.ReceiveValue
  FROM tmp_receive tr
    LEFT JOIN tmp_termination tt
      ON tr.DateTimeValue = tt.DateTimeValue
  UNION
  SELECT
    tr.DateTimeValue,
    tr.TerminationValue,
    tt.ReceiveValue
  FROM tmp_termination tr
    LEFT JOIN tmp_receive tt
      ON tr.DateTimeValue = tt.DateTimeValue;

END
$$

